import React from "react";
import "./Login.scss";
import { Link } from "react-router-dom";

const Login = () => {
  return (
    <div className="login">
      <div className="left">
        <H2 className="top">TRUY CẬP VÀO TÀI KHOẢN CỦA BẠN</H2>
        
        <form className="bot">
          <div>
            <input type="text" id="email" placeholder="Địa chỉ Email" />
          </div>
          <div>
            <input type="password" id="password" placeholder="Mật khẩu" />
          </div>
          <button type="submit">Login</button>
        </form>
      </div>
      <div className="right">
        <span>CẦN MỘT TÀI KHOẢN?</span>
        <div>
          <Link to="/signup">
            <button>Đăng ký</button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
